import 'controller/main_page_container_controller.dart';import 'package:flutter/material.dart';import 'package:raj_s_application5/core/app_export.dart';import 'package:raj_s_application5/presentation/catalog_one_page/catalog_one_page.dart';import 'package:raj_s_application5/presentation/favorites_modules_page/favorites_modules_page.dart';import 'package:raj_s_application5/presentation/main_page/main_page.dart';import 'package:raj_s_application5/presentation/my_bag_page/my_bag_page.dart';import 'package:raj_s_application5/presentation/my_profile_my_orders_page/my_profile_my_orders_page.dart';import 'package:raj_s_application5/widgets/custom_bottom_bar.dart';class MainPageContainerScreen extends GetWidget<MainPageContainerController> {@override Widget build(BuildContext context) { return SafeArea(child: Scaffold(backgroundColor: ColorConstant.gray50, body: Navigator(key: Get.nestedKey(1), initialRoute: AppRoutes.mainPage, onGenerateRoute: (routeSetting) => GetPageRoute(page: () => getCurrentPage(routeSetting.name!), transition: Transition.noTransition)), bottomNavigationBar: CustomBottomBar(onChanged: (BottomBarEnum type) {Get.toNamed(getCurrentRoute(type), id: 1);}))); } 
String getCurrentRoute(BottomBarEnum type) { switch (type) {case BottomBarEnum.Home: return AppRoutes.mainPage; case BottomBarEnum.Shop: return AppRoutes.catalogOnePage; case BottomBarEnum.Bag: return AppRoutes.myBagPage; case BottomBarEnum.Favorites: return AppRoutes.favoritesModulesPage; case BottomBarEnum.Profile: return AppRoutes.myProfileMyOrdersPage; default: return "/";} } 
Widget getCurrentPage(String currentRoute) { switch (currentRoute) {case AppRoutes.mainPage: return MainPage(); case AppRoutes.catalogOnePage: return CatalogOnePage(); case AppRoutes.myBagPage: return MyBagPage(); case AppRoutes.favoritesModulesPage: return FavoritesModulesPage(); case AppRoutes.myProfileMyOrdersPage: return MyProfileMyOrdersPage(); default: return DefaultWidget();} } 
 }
